mapper.select:
select * from (
	select a.*,rownum rn from (
		select * from ad where status in(1,0) order by aid
	)a where rownum<=#{max,jdbcType=NUMERIC}
)b where rn>#{min,jdbcType=NUMERIC}

select * from (
	select a.*,rownum rn from (
		select n.*,aposition from admessage n,ad where n.aid=ad.aid order by amid
	)a where rownum<=#{max,jdbcType=NUMERIC}
)b where rn>#{min,jdbcType=NUMERIC}
